import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css'; // Import the CSS file

const App = () => {
  const [recipes, setRecipes] = useState([]);
  const [searchQuery, setSearchQuery] = useState(''); // Default search query
  const [displayedRecipes, setDisplayedRecipes] = useState(4); // Number of displayed recipes
  const [selectedRecipeId, setSelectedRecipeId] = useState(null);

  const handleRecipeClick = (recipeId) => {
    setSelectedRecipeId(recipeId);
  };

  const handleMoreClick = () => {
    setDisplayedRecipes(displayedRecipes + 4);
  };
  const handleSearch = async () => {
    try {
      const response = await axios.get(
        'https://api.edamam.com/api/recipes/v2',
        {
          params: {
            type: 'public',
            q: searchQuery,
            app_id: 'af0cb042', // Your app_id
            app_key: 'c52148fdd9f9782411ded7cb50d4ba91', // Your app_key
          },
        }
      );
      console.log(response.data.hits); // Check if data is received
      setRecipes(response.data.hits);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    handleSearch();
  }, []); // This will run on component mount

  const handleSubmit = (event) => {
    event.preventDefault();
    handleSearch();
  };

  return (
    <div className="app">
      <h1>Recipe Generator</h1>
      <form onSubmit={handleSubmit}>
        {/* ... Your search form ... */}
      </form>
      {recipes.length === 0 && (
        <img src={require('./a.png')} alt="Initial" />
      )}
      <div className="recipe-list">
        {recipes.slice(0, displayedRecipes).map((recipe) => (
          <div
            className={`recipe-card ${
              selectedRecipeId === recipe.recipe.uri ? 'selected' : ''
            }`}
            key={recipe.recipe.uri}
            onClick={() => handleRecipeClick(recipe.recipe.uri)}
          >
            <h2>{recipe.recipe.label}</h2>
            <p>{recipe.recipe.source}</p>
            <img src={recipe.recipe.image} alt={recipe.recipe.label} />
          </div>
        ))}
      </div>
      {selectedRecipeId && (
        <div className="full-recipe-details">
          {/* Render the full recipe details here */}
          {/* You can use the same structure and styling as the recipe cards */}
          {/* Fetch the detailed recipe using the selectedRecipeId */}
        </div>
      )}
      {displayedRecipes < recipes.length && (
        <button onClick={handleMoreClick}>More</button>
      )}
    </div>
  );
};

export default App;
