import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css'; // Import the CSS file
import Fuse from 'fuse.js';

const App = () => {
  const [recipes, setRecipes] = useState([]);
  const [searchQuery, setSearchQuery] = useState(''); // Default search query
  const [searchResults, setSearchResults] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);

  const handleRecipeClick = (recipe) => {
    setSelectedRecipe(recipe);
    window.open(recipe.recipe.url, '_blank');
  };

  const handleSearch = async () => {
    try {
      const response = await axios.get(
        'https://api.edamam.com/api/recipes/v2',
        {
          params: {
            type: 'public',
            q: searchQuery,
            app_id: 'af0cb042', // Your app_id
            app_key: 'c52148fdd9f9782411ded7cb50d4ba91', // Your app_key
          },
        }
      );

      // Normalize the search query and the data
      const normalizedQuery = searchQuery.toLowerCase().trim();
      const normalizedData = response.data.hits.map((hit) => ({
        ...hit,
        recipe: {
          ...hit.recipe,
          label: hit.recipe.label.toLowerCase(),
        },
      }));

      // Create a fuzzy search instance
      const options = {
        keys: ['recipe.label'], // Adjust the keys to match your data structure
      };
      const fuse = new Fuse(normalizedData, options);

      // Perform the fuzzy search with the normalized query
      const fuzzyResults = fuse.search(normalizedQuery);
      setSearchResults(fuzzyResults.map((result) => result.item));

      setRecipes(response.data.hits);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    handleSearch();
  }, []); // This will run on component mount

  const handleSubmit = (event) => {
    event.preventDefault();
    handleSearch();
  };

  return (
    <div className="app">
      <h1>Recipe Generator</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={searchQuery}
          onChange={(event) => setSearchQuery(event.target.value)}
          placeholder="Enter a recipe keyword"
        />
        <button type="submit">Search</button>
      </form>
      {recipes.length === 0 && (
        <img src={require('./a.png')} alt="Initial" style={{ marginRight: '100px' }} />
      )}
      <div className="recipe-list">
        {searchResults.map((recipe) => (
          <div
            className="recipe-card"
            key={recipe.recipe.uri}
            onClick={() => handleRecipeClick(recipe)}
          >
            <h2>{recipe.recipe.label}</h2>
            <p>{recipe.recipe.source}</p>
            <img src={recipe.recipe.image} alt={recipe.recipe.label} />
          </div>
        ))}
      </div>
      {selectedRecipe && (
        <div className="full-recipe-details">
          <h2>{selectedRecipe.recipe.label}</h2>
          {/* Render other full recipe details here */}
        </div>
      )}
    </div>
  );
};

export default App;
