import React, { useState, useEffect } from 'react';
import axios from 'axios';
import RecipeList from './components/RecipeList';

const App = () => {
  const [recipes, setRecipes] = useState([]);
  const [query, setQuery] = useState(''); // State for user input

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get(
          'https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/complexSearch',
          {
            params: {
              query: query,
              },
              headers: {
                'X-RapidAPI-Key': 'bef445d691mshab3428b890aa2a1p1997f5jsn755a3329d2e7',
                'X-RapidAPI-Host': 'spoonacular-recipe-food-nutrition-v1.p.rapidapi.com'
              }
          }
        );
        console.log(response.data); // Check if data is received
        setRecipes(response.data.results);
      } catch (error) {
        console.error(error);
      }
    };
  
    fetchRecipes();
  }, [query]);

  const handleInputChange = event => {
    setQuery(event.target.value); // Update the query state on input change
  };

  return (
    <div className="app">
      <h1>Recipe Website</h1>
      <form>
        <label>
          Search for recipes:
          <input type="text" value={query} onChange={handleInputChange} />
        </label>
        <button type="submit">Search</button>
      </form>
      <RecipeList recipes={recipes} />
    </div>
  );
};

export default App;
