import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css'; // Import the CSS file

const App = () => {
  const [recipes, setRecipes] = useState([]);
  const [searchQuery, setSearchQuery] = useState(''); // Default search query
  const [displayedRecipes, setDisplayedRecipes] = useState(5); // Number of displayed recipes

  const handleMoreClick = () => {
    setDisplayedRecipes(displayedRecipes + 5);
  };
  const handleSearch = async () => {
    try {
      const response = await axios.get(
        'https://api.edamam.com/api/recipes/v2',
        {
          params: {
            type: 'public',
            q: searchQuery,
            app_id: 'af0cb042', // Your app_id
            app_key: 'c52148fdd9f9782411ded7cb50d4ba91', // Your app_key
          },
        }
      );
      console.log(response.data.hits); // Check if data is received
      setRecipes(response.data.hits);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    handleSearch();
  }, []); // This will run on component mount

  const handleSubmit = (event) => {
    event.preventDefault();
    handleSearch();
  };

  return (
    <div className="app">
    <h1>Recipe Generator</h1>
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        value={searchQuery}
        onChange={(event) => setSearchQuery(event.target.value)}
        placeholder="Enter a recipe keyword"
      />
      <button type="submit">Search</button>
    </form>
    {recipes.length === 0 && (
      <img src={require('./a.png')} alt="Initial" />
    )}
      <div className="recipe-list">
        {recipes.map((recipe) => (
          <div className="recipe-card" key={recipe.recipe.uri}>
            <h2>{recipe.recipe.label}</h2>
            <p>{recipe.recipe.source}</p>
            <img src={recipe.recipe.image} alt={recipe.recipe.label} />
            {/* Render other recipe details as needed */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
